static CURLcode curl_easy_streaming_plugin_message(CURL *curl_handle, const gchar *janus_url,
													gboolean (*message_request_cb)(json_t *request, gpointer data),
													gboolean (*message_response_cb)(json_t *response, gpointer data),
													gpointer data) {

	json_t *json_object_request = NULL;
	json_t *json_object_response = NULL;
	json_t *json_object_data = NULL;
	json_t *json_object_plugin_handle_id = NULL;
	gchar *url = NULL;
	CURLcode return_value = CURLE_OK;
	guint64 session_id = 0;
	guint64 plugin_handle_id = 0;

	do
	{
		// allocation
		// create request
		json_object_request = json_janus_request("create");
		if (!json_object_request) {
			JANUS_LOG(LOG_ERR, "Could not initiate create janus request.\n");
			break;
		}
		return_value = curl_easy_post_json_request(curl_handle, janus_url, json_object_request, &json_object_response);
		json_decref(json_object_request);
		json_object_request = NULL;
		if (CURLE_OK != return_value) {
			JANUS_LOG(LOG_ERR, "Failed to post json request (error=%u).\n", return_value);
			break;
		}

		// handle response
		json_object_data = json_object_get(json_object_response, "data");
		if (!json_is_object(json_object_data)) {
			JANUS_LOG(LOG_ERR, "Could not get data json object from the response.\n");
			break;
		}
		json_t *json_object_session_id = json_object_get(json_object_data, "id");
		if (!json_is_integer(json_object_session_id)) {
			JANUS_LOG(LOG_ERR, "Could not get integer session id from the data.\n");
			break;
		}
		session_id = json_integer_value(json_object_session_id);
		// allocation
		url = g_strdup_printf("%s/%"SCNu64, janus_url, session_id);
		json_decref(json_object_response);
		json_object_response = NULL;

		// allocation
		// attach request
		json_object_request = json_janus_request("attach");
		if (!json_object_request) {
			JANUS_LOG(LOG_ERR, "Could not initiate attach janus request.\n");
			break;
		}
		if (json_object_set_new(json_object_request, "plugin", json_string(JANUS_STREAMING_PACKAGE))) {
			JANUS_LOG(LOG_ERR, "Could not set object plugin with json string in the json request.\n");
			break;
		}
		return_value = curl_easy_post_json_request(curl_handle, url, json_object_request, &json_object_response);		
		g_free(url);
		url = NULL;
		json_decref(json_object_request);
		json_object_request = NULL;
		if (CURLE_OK != return_value) {
			JANUS_LOG(LOG_ERR, "Failed to post json request (error=%u).\n", return_value);
			break;
		}

		// handle response
		json_object_data = json_object_get(json_object_response, "data");
		if (!json_is_object(json_object_data)) {
			JANUS_LOG(LOG_ERR, "Could not get data json object from the response.\n");
			break;
		}
		json_t *json_object_plugin_handle_id = json_object_get(json_object_data, "id");
		if (!json_is_integer(json_object_plugin_handle_id)) {
			JANUS_LOG(LOG_ERR, "Could not get integer session id from the data.\n");
			break;
		}
		plugin_handle_id = json_integer_value(json_object_plugin_handle_id);
		// allocation
		url = g_strdup_printf("%s/%"SCNu64"/%"SCNu64, janus_url, session_id, plugin_handle_id);
		json_decref(json_object_response);
		json_object_response = NULL;

		// allocation
		// message request
		json_object_request = json_janus_request("message");
		if (!json_object_request) {
			JANUS_LOG(LOG_ERR, "Could not initiate message janus request.\n");
			break;
		}
		if (message_request_cb) {
			if (!message_request_cb(json_object_request, data)) {
				JANUS_LOG(LOG_ERR, "Could not send janus request.\n");
				break;
			}
		}
		return_value = curl_easy_post_json_request(curl_handle, url, json_object_request, &json_object_response);
		g_free(url);
		url = NULL;
		json_decref(json_object_request);
		json_object_request = NULL;
		if (CURLE_OK != return_value) {		
			JANUS_LOG(LOG_ERR, "Failed to post json request (error=%u).\n", return_value);
			break;
		}
		// handle response
		if (message_response_cb) {
			if (!message_response_cb(json_object_response, data)) {
				JANUS_LOG(LOG_ERR, "Could not handle janus response.\n");
				break;
			}
		}
		json_decref(json_object_response);
		json_object_response = NULL;

		// create detach request
		json_object_request = json_janus_request("detach");
		if (!json_object_request) {
			JANUS_LOG(LOG_ERR, "Could not initiate dettach janus request.\n");
			break;
		}
		if (json_object_set_new(json_object_request, "plugin", json_string(JANUS_STREAMING_PACKAGE))) {
			JANUS_LOG(LOG_ERR, "Could not set object plugin with json string in the json request.\n");
			break;
		}
		// allocation
		url = g_strdup_printf("%s/%"SCNu64"/%"SCNu64, janus_url, session_id, plugin_handle_id);
		return_value = curl_easy_post_json_request(curl_handle, url, json_object_request, &json_object_response);
		g_free(url);
		url = NULL;		
		if (CURLE_OK != return_value) {		
			JANUS_LOG(LOG_ERR, "Failed to post json request (error=%u).\n", return_value);
			break;
		}
	} while (0);

	// cleanup
	if (json_object_request) {	
		json_decref(json_object_request);
		json_object_request = NULL;
	}
	if (json_object_response) {
		json_decref(json_object_response);
		json_object_response = NULL;
	}
	if (json_object_plugin_handle_id) {
		json_decref(json_object_plugin_handle_id);
		json_object_plugin_handle_id = NULL;
	}
	if (url) {
		g_free(url);
		url = NULL;
	}

	return return_value;
}

typedef struct {

	gchar *id;
	gchar *pipeline_string;

} pipeline_data_t;

static void teardown_pipeline(janus_streaming_mountpoint *mountpoint) {

	CURL *curl_handle = NULL;

	do {
		if (!mountpoint) {
			JANUS_LOG(LOG_ERR, "Input parameter mountpoint was null.\n");
			break;
		}
		janus_mutex_lock(&transcode_main_loops_mutex);		
		GMainLoop *main_loop = g_hash_table_lookup(transcode_main_loops, mountpoint->id);
		if (!main_loop) {
			JANUS_LOG(LOG_ERR, "Could not find mountpoint.\n");
		}
		else {
			if (!g_hash_table_remove(transcode_main_loops, mountpoint->id)) {
				JANUS_LOG(LOG_ERR, "Could not remove main loop from the table.\n");
			}
			g_main_loop_quit(main_loop);
		}
		janus_mutex_unlock(&transcode_main_loops_mutex);
		// allocation		
		curl_handle = curl_easy_init();
		if (!curl_handle) {
			JANUS_LOG(LOG_ERR, "Could not initialize curl.\n");
		}
		else {
			CURLcode  curl_code = curl_easy_streaming_plugin_message(curl_handle, janus_endpoint, message_mountpoint_destroy_request,
																	NULL, mountpoint);
			if (CURLE_OK != curl_code) {
				JANUS_LOG(LOG_ERR, "Failed to destroy mountpoint.\n");
			}
			curl_easy_cleanup(curl_handle);
			curl_handle = NULL;
		}
		remove_port(&transcode_ports, ((janus_streaming_rtp_source *)mountpoint->source)->audio_port);
		remove_port(&transcode_ports, ((janus_streaming_rtp_source *)mountpoint->source)->video_port);
	}
	while(0);

	// cleanup
	if (curl_handle) {
		curl_easy_cleanup(curl_handle);
		curl_handle = NULL;
	}

}

static gboolean on_eos(GstBus *bus, GstMessage *message, gpointer data)
{
	gchar *id = (gchar *)data;
	janus_mutex_lock(&mountpoints_mutex);
	janus_streaming_mountpoint *mp = g_hash_table_lookup(mountpoints, id);
	janus_mutex_unlock(&mountpoints_mutex);
	teardown_pipeline(mp);
	return TRUE;
}

static gboolean on_error(GstBus *bus, GstMessage *message, gpointer data)
{
	GError *error = NULL;
	gchar *debug = NULL;

	gst_message_parse_error(message, &error, &debug);
	JANUS_LOG(LOG_ERR, "Error: %s\n", error->message ? error->message : "??");
	JANUS_LOG(LOG_DBG, "Debugging info: %s\n", debug ? debug : "??");
	g_error_free(error);
	g_free(debug);
	on_eos(bus, message, data);
	return TRUE;
}

static gpointer transcode_handler(gpointer data) {

	GstElement *pipeline = NULL;	
	GstBus *bus = NULL;
	GSource *bus_source = NULL;
	GMainContext *context = NULL;
	GMainLoop *main_loop = NULL;
	pipeline_data_t *pipeline_data = (pipeline_data_t *)data;

	do
	{
		if (!pipeline_data) {
			JANUS_LOG(LOG_ERR, "Input parameter data is null.\n");
			break;
		}
		if (!pipeline_data->pipeline_string) {
			JANUS_LOG(LOG_ERR, "Invalid format of input parameter.\n");
			break;
		}
		GError *error = NULL;
		pipeline = gst_parse_launch(pipeline_data->pipeline_string, &error);
		g_free(pipeline_data->pipeline_string);
		pipeline_data->pipeline_string = NULL;
		if (!pipeline) {
			if (error) {
				JANUS_LOG(LOG_ERR, "Got error %d (%s) trying to parse gstreamer pipeline...\n", error->code, error->message ? error->message : "??");
			}
			else {
				JANUS_LOG(LOG_ERR, "Could not parse the pipeline....\n");
			}
			break;
		}
		if (GST_STATE_CHANGE_FAILURE == gst_element_set_state(pipeline, GST_STATE_PLAYING)) {
			JANUS_LOG(LOG_ERR, "Could not change state of pipeline to PLAYING state.\n");
			break;
		}
		GstState state;
		GstStateChangeReturn ret;
		do
		{
			if (GST_STATE_CHANGE_FAILURE == (ret = gst_element_get_state(pipeline,
				&state, NULL, GST_CLOCK_TIME_NONE))) {
				JANUS_LOG(LOG_ERR, "Could not change state of pipeline to PLAYING state.\n");
				break;
			}
		}
		while (GST_STATE_PLAYING != state);
		if (GST_STATE_CHANGE_FAILURE == ret) {
			break;
		}
		if (NULL == (bus = gst_element_get_bus (pipeline))) {
			JANUS_LOG(LOG_ERR, "Could not get the bus.\n");
			break;
		}
		if (NULL == (bus_source = gst_bus_create_watch(bus))) {
			JANUS_LOG(LOG_ERR, "Could not create a watch.\n");
			break;
		}
		if (NULL == (context = g_main_context_new())) {
			JANUS_LOG(LOG_ERR, "Could not create new context.\n");
			break;
		}
		g_main_context_push_thread_default(context);
		g_source_set_callback(bus_source, (GSourceFunc) gst_bus_async_signal_func, NULL, NULL);
		g_source_attach(bus_source, context);
		g_source_unref(bus_source);
		bus_source = NULL;
		janus_mutex_lock(&transcode_main_loops_mutex);
		main_loop = g_main_loop_new(context, FALSE);
		g_signal_connect (G_OBJECT (bus), "message::eos", (GCallback)on_eos, pipeline_data->id);
		g_signal_connect (G_OBJECT (bus), "message::error", (GCallback)on_error, pipeline_data->id);
		gst_object_unref(bus);
		bus = NULL;
		g_hash_table_insert(transcode_main_loops, pipeline_data->id, main_loop);
		janus_mutex_unlock(&transcode_main_loops_mutex);		
		g_free(pipeline_data);
		pipeline_data = NULL;
		g_main_loop_run(main_loop);
		if (GST_STATE_CHANGE_FAILURE == gst_element_set_state(pipeline, GST_STATE_NULL)) {
			JANUS_LOG(LOG_ERR, "Could not change state of pipeline to NULL state.\n");
		}
		else { 
			do
			{
				if (GST_STATE_CHANGE_FAILURE == (ret = gst_element_get_state(pipeline,
					&state, NULL, GST_CLOCK_TIME_NONE))) {
					JANUS_LOG(LOG_ERR, "Could not change state of pipeline to NULL state.\n");
					break;
				}
			}
			while (GST_STATE_NULL != state);
		}
		gst_object_unref(pipeline);
		pipeline = NULL;
		g_main_loop_unref(main_loop);
		main_loop = NULL;
		g_main_context_pop_thread_default(context);
		g_main_context_unref(context);
		context = NULL;
	}
	while(0);

	// cleanup
	if (bus_source) {
		g_source_unref(bus_source);
		bus_source = NULL;
	}
	if (bus) {
		gst_object_unref(bus);
		bus = NULL;
	}
	if (main_loop) {
		g_main_loop_unref(main_loop);
		main_loop = NULL;
	}
	if (context) {
		g_main_context_unref(context);
		context = NULL;
	}
	if (pipeline) {
		gst_object_unref(pipeline);
		pipeline = NULL;
	}
	if (pipeline_data) {
		if (pipeline_data->pipeline_string) {
			g_free(pipeline_data->pipeline_string);
			pipeline_data->pipeline_string = NULL;
		}
		g_free(pipeline_data);
		pipeline_data = NULL;
	}

	return NULL;
}

static void setup_pipeline(const gchar *id) {

	CURL *curl_handle = NULL; 
	janus_streaming_rtp_source *rtp_source = NULL;
	janus_streaming_mountpoint *mountpoint = NULL;
	gchar *source = NULL;

	do {
		if (!registry_endpoint) {
			JANUS_LOG(LOG_WARN, "Registry endpoint not specified. Trying local registry.\n");
			// allocation
			source = get_source_from_local_registry_by_id(id);
		}
		else {
			// allocation
			source = get_source_from_registry_by_id(registry_endpoint, id);
		}
		if (!source) {
			JANUS_LOG(LOG_ERR, "Could not get source.\n");
			break;
		}

		// allocation
		guint audio_port = get_port(&transcode_ports);
		if (!audio_port) {
			JANUS_LOG(LOG_ERR, "Could not allocate audio port.\n");
			break;
		}

		// allocation
		guint video_port = get_port(&transcode_ports);
		if (!video_port) {
			JANUS_LOG(LOG_ERR, "Could not allocate video port.\n");
			break;
		}

		// allocation
		curl_handle = curl_easy_init();
		if (!curl_handle) {
			JANUS_LOG(LOG_ERR, "Could not initialize curl.\n");
			break;
		}

		// allocation
		rtp_source = g_malloc0(sizeof(janus_streaming_rtp_source));
		if (!rtp_source) {
			JANUS_LOG(LOG_ERR, "Could not allocate rtp source.\n");
			break;
		}
		rtp_source->audio_port = audio_port;
		rtp_source->video_port = video_port;
		mountpoint = g_malloc0(sizeof(janus_streaming_mountpoint));
		if (!rtp_source) {
			JANUS_LOG(LOG_ERR, "Could not allocate mountpoint.\n");
			break;
		}
		mountpoint->source = rtp_source;
		mountpoint->id = g_strdup(id);
		mountpoint->is_private = FALSE;

		CURLcode curl_code = curl_easy_streaming_plugin_message(curl_handle, janus_endpoint, message_mountpoint_create_request,
																message_mountpoint_create_response, mountpoint);

		g_free(mountpoint->id);
		mountpoint->id = NULL;
		g_free(mountpoint->source);
		mountpoint->source = NULL;
		g_free(mountpoint);
		mountpoint = NULL;
		// cleanup curl
		curl_easy_cleanup(curl_handle);
		curl_handle = NULL;

		if (CURLE_OK != curl_code) {
			JANUS_LOG(LOG_ERR, "Could not create mountpoint.\n");
			break;
		}

		// allocation - deallocated within the thread
		pipeline_data_t *pipeline_data = g_new0(pipeline_data_t, 1);
		if (!pipeline_data) {
			JANUS_LOG(LOG_ERR, "Could not allocate pipeline data.\n");
			break;
		}
		// allocation - deallocated when removing form the transcode_main_loops hash table
		pipeline_data->id = g_strdup(id);
		// allocation - deallocated within the thread
		pipeline_data->pipeline_string = g_strdup_printf(
														"uridecodebin uri=\"%s\" name=src ! audioconvert ! audioresample ! "
														"audio/x-raw,channels=1,rate=16000 ! opusenc bitrate=20000 ! rtpopuspay ! udpsink port=%d src. ! "
														"x264enc ! video/x-h264, profile=baseline ! rtph264pay ! udpsink port=%d",
														source, audio_port, video_port);
		g_free(source);
		source = NULL;
		GError *error = NULL;
		// allocation
		GThread *thread_handler = g_thread_try_new("transcode handler", transcode_handler, pipeline_data, &error);
		if (!thread_handler) {
			if (error) {
				JANUS_LOG(LOG_ERR, "Got error %d (%s) trying to launch the transcode handler thread...\n", error->code,
				 		error->message ? error->message : "??");
			} else {
				JANUS_LOG(LOG_ERR, "Failed to start transcode handler...\n");
			}
			g_free(pipeline_data->id);
			pipeline_data->id = NULL;
			g_free(pipeline_data->pipeline_string);
			pipeline_data->pipeline_string = NULL;
			g_free(pipeline_data);
			pipeline_data = NULL;
			break;
		}
		janus_mutex_lock(&transcode_threads_mutex);
		g_hash_table_insert(transcode_threads, (gchar *)id, thread_handler);
		janus_mutex_unlock(&transcode_threads_mutex);
	}
	while(0);

	// cleanup
	if (source) {
		g_free(source);
		source = NULL;
	}
	if (curl_handle) {
		curl_easy_cleanup(curl_handle);
		curl_handle = NULL;
	}
	if (mountpoint) {
		if (mountpoint->source) {
			g_free(mountpoint->source);
			mountpoint->source = NULL;
		}
		g_free(mountpoint);
		mountpoint = NULL;
	}

}

/* Streaming watchdog/garbage collector (sort of) */
void *janus_streaming_watchdog(void *data);
void *janus_streaming_watchdog(void *data) {
	JANUS_LOG(LOG_INFO, "Streaming watchdog started\n");
	gint64 now = 0;
	while(g_atomic_int_get(&initialized) && !g_atomic_int_get(&stopping)) {
		janus_mutex_lock(&sessions_mutex);
		/* Iterate on all the sessions */
		now = janus_get_monotonic_time();
		if(old_sessions != NULL) {
			GList *sl = old_sessions;
			JANUS_LOG(LOG_HUGE, "Checking %d old Streaming sessions...\n", g_list_length(old_sessions));
			while(sl) {
				janus_streaming_session *session = (janus_streaming_session *)sl->data;
				if(!session) {
					sl = sl->next;
					continue;
				}
				if(now-session->destroyed >= 5*G_USEC_PER_SEC) {
					/* We're lazy and actually get rid of the stuff only after a few seconds */
					JANUS_LOG(LOG_VERB, "Freeing old Streaming session\n");
					GList *rm = sl->next;
					old_sessions = g_list_delete_link(old_sessions, sl);
					sl = rm;
					session->handle = NULL;
					g_free(session);
					session = NULL;
					continue;
				}
				sl = sl->next;
			}
		}
		janus_mutex_unlock(&sessions_mutex);
		janus_mutex_lock(&mountpoints_mutex);
		/* Iterate on all the mountpoints */
		if(old_mountpoints != NULL) {
			GList *sl = old_mountpoints;
			JANUS_LOG(LOG_HUGE, "Checking %d old Streaming mountpoints...\n", g_list_length(old_mountpoints));
			while(sl) {
				janus_streaming_mountpoint *mountpoint = (janus_streaming_mountpoint *)sl->data;
				if(!mountpoint) {
					sl = sl->next;
					continue;
				}
				if(now-mountpoint->destroyed >= 5*G_USEC_PER_SEC) {
					/* We're lazy and actually get rid of the stuff only after a few seconds */
					JANUS_LOG(LOG_VERB, "Freeing old Streaming mountpoint\n");
					GList *rm = sl->next;
					old_mountpoints = g_list_delete_link(old_mountpoints, sl);
					sl = rm;
					janus_streaming_mountpoint_free(mountpoint);
					mountpoint = NULL;
					continue;
				}
				sl = sl->next;
			}
		}
		janus_mutex_unlock(&mountpoints_mutex);
		g_usleep(500000);
	}
	JANUS_LOG(LOG_INFO, "Streaming watchdog stopped\n");
	return NULL;
}


/* Plugin implementation */
int janus_streaming_init(janus_callbacks *callback, const char *config_path) {
	gst_init(NULL, NULL);
#ifdef HAVE_LIBCURL	
	curl_global_init(CURL_GLOBAL_ALL);
#endif	
	if(g_atomic_int_get(&stopping)) {
		/* Still stopping from before */
		return -1;
	}
	if(callback == NULL || config_path == NULL) {
		/* Invalid arguments */
		return -1;
	}

	/* Read configuration */
	char filename[255];
	g_snprintf(filename, 255, "%s/%s.cfg", config_path, JANUS_STREAMING_PACKAGE);
	JANUS_LOG(LOG_VERB, "Configuration file: %s\n", filename);
	config = janus_config_parse(filename);
	config_folder = config_path;
	if(config != NULL)
		janus_config_print(config);
	janus_mutex_init(&config_mutex);
	
	mountpoints = g_hash_table_new(g_str_hash, g_str_equal);
	transcode_threads = g_hash_table_new(g_str_hash, g_str_equal);
	transcode_main_loops = g_hash_table_new_full(g_str_hash, g_str_equal, g_str_destroy, NULL);
	transcode_sources = g_hash_table_new_full(g_str_hash, g_str_equal, g_str_destroy, g_str_destroy);
	janus_mutex_init(&mountpoints_mutex);
	janus_mutex_init(&transcode_threads_mutex);
	janus_mutex_init(&transcode_main_loops_mutex);
	janus_mutex_init(&transcode_sources_mutex);	
	janus_mutex_init(&(transcode_ports.mutex));
	/* Parse configuration to populate the mountpoints */
	if(config != NULL) {
		uint16_t rtp_min_port = 0;
		uint16_t rtp_max_port = 0;
		gboolean next = FALSE;
		janus_config_item *item = janus_config_get_item_drilldown(config, "general", "rtp_port_range");
		if(item && item->value) {
			next = TRUE;
			/* Split in min and max port */
			char *maxport = strrchr(item->value, '-');
			if(maxport != NULL) {
				*maxport = '\0';
				maxport++;
				rtp_min_port = atoi(item->value);
				rtp_max_port = atoi(maxport);
				maxport--;
				*maxport = '-';
			}
			if(rtp_min_port > rtp_max_port) {
				int temp_port = rtp_min_port;
				rtp_min_port = rtp_max_port;
				rtp_max_port = temp_port;
			}
			if(rtp_max_port == 0)
				rtp_max_port = 65535;
			JANUS_LOG(LOG_INFO, "RTP port range: %u -- %u\n", rtp_min_port, rtp_max_port);
		}
		if (!set_port_range(&transcode_ports, rtp_min_port, rtp_max_port)) {
			set_port_range(&transcode_ports, 40001, 50000);
		}
		item = janus_config_get_item_drilldown(config, "general", "janus_endpoint");
		if (item && item->value) {
			next = TRUE;
			janus_endpoint = item->value;
		}
		else {
			janus_endpoint = "http://localhost:8088/janus";
		}
		item = janus_config_get_item_drilldown(config, "general", "registry_endpoint");
		if (item && item->value) {
			next = TRUE;
			registry_endpoint = item->value;
		}
		GList *cl = janus_config_get_categories(config);
		if (next) {
			cl = cl->next;
		}
		while(cl != NULL) {
			janus_config_category *cat = (janus_config_category *)cl->data;
			if(cat->name == NULL) {
				cl = cl->next;
				continue;
			}
			JANUS_LOG(LOG_VERB, "Adding stream '%s'\n", cat->name);
			janus_config_item *type = janus_config_get_item(cat, "type");
			if(type == NULL || type->value == NULL) {
				JANUS_LOG(LOG_WARN, "  -- Invalid type, skipping stream '%s'...\n", cat->name);
				cl = cl->next;
				continue;
			}
			if(!strcasecmp(type->value, "source")) {
				janus_config_item *id = janus_config_get_item(cat, "id");
				janus_config_item *uri = janus_config_get_item(cat, "uri");
				if (!id || !id->value) {
					JANUS_LOG(LOG_VERB, "Missing id for stream '%s', will generate a random one...\n", cat->name);					
				}
				else {
					janus_mutex_lock(&transcode_sources_mutex);
					gchar *source = g_hash_table_lookup(transcode_sources, id->value);
					janus_mutex_unlock(&transcode_sources_mutex);
					if(source != NULL) {
						JANUS_LOG(LOG_ERR, "A source with the provided ID %s already exists, skipping '%s'\n", id->value, cat->name);
						cl = cl->next;
						continue;
					}
				}
				gchar *id_value = NULL;
				if (!id || !id->value) {
					JANUS_LOG(LOG_VERB, "Missing id, will generate a random one...\n");
					while(id_value == NULL) {
						id_value = random_id();
						if(g_hash_table_lookup(transcode_sources, id_value) != NULL) {
							/* ID already in use, try another one */
							id_value = NULL;
						}
					}
				}
				else {
					id_value = g_strdup(id->value);
				}
				gchar *uri_value = NULL;
				if (!uri || !uri->value) {
					cl = cl->next;
					continue;
				}
				else {
					uri_value = g_strdup(uri->value);
				}
				janus_mutex_lock(&transcode_sources_mutex);
				g_hash_table_insert(transcode_sources, id_value, uri_value);
				janus_mutex_unlock(&transcode_sources_mutex);
			}
			else if(!strcasecmp(type->value, "rtp")) {
				/* RTP live source (e.g., from gstreamer/ffmpeg/vlc/etc.) */
				janus_config_item *id = janus_config_get_item(cat, "id");
				janus_config_item *desc = janus_config_get_item(cat, "description");
				janus_config_item *priv = janus_config_get_item(cat, "is_private");
				janus_config_item *secret = janus_config_get_item(cat, "secret");
				janus_config_item *pin = janus_config_get_item(cat, "pin");
				janus_config_item *audio = janus_config_get_item(cat, "audio");
				janus_config_item *video = janus_config_get_item(cat, "video");
				janus_config_item *amcast = janus_config_get_item(cat, "audiomcast");
				janus_config_item *aport = janus_config_get_item(cat, "audioport");
				janus_config_item *acodec = janus_config_get_item(cat, "audiopt");
				janus_config_item *artpmap = janus_config_get_item(cat, "audiortpmap");
				janus_config_item *afmtp = janus_config_get_item(cat, "audiofmtp");
				janus_config_item *vmcast = janus_config_get_item(cat, "videomcast");
				janus_config_item *vport = janus_config_get_item(cat, "videoport");
				janus_config_item *vcodec = janus_config_get_item(cat, "videopt");
				janus_config_item *vrtpmap = janus_config_get_item(cat, "videortpmap");
				janus_config_item *vfmtp = janus_config_get_item(cat, "videofmtp");
				janus_config_item *vkf = janus_config_get_item(cat, "videobufferkf");
				gboolean is_private = priv && priv->value && janus_is_true(priv->value);
				gboolean doaudio = audio && audio->value && janus_is_true(audio->value);
				gboolean dovideo = video && video->value && janus_is_true(video->value);
				gboolean bufferkf = video && vkf && vkf->value && janus_is_true(vkf->value);
				if(!doaudio && !dovideo) {
					JANUS_LOG(LOG_ERR, "Can't add 'rtp' stream '%s', no audio or video have to be streamed...\n", cat->name);
					cl = cl->next;
					continue;
				}
				if(doaudio &&
						(aport == NULL || aport->value == NULL ||
						acodec == NULL || acodec->value == NULL ||
						artpmap == NULL || artpmap->value == NULL)) {
					JANUS_LOG(LOG_ERR, "Can't add 'rtp' stream '%s', missing mandatory information for audio...\n", cat->name);
					cl = cl->next;
					continue;
				}
				if(dovideo &&
						(vport == NULL || vport->value == NULL ||
						vcodec == NULL || vcodec->value == NULL ||
						vrtpmap == NULL || vrtpmap->value == NULL)) {
					JANUS_LOG(LOG_ERR, "Can't add 'rtp' stream '%s', missing mandatory information for video...\n", cat->name);
					cl = cl->next;
					continue;
				}
				if(id == NULL || id->value == NULL) {
					JANUS_LOG(LOG_VERB, "Missing id for stream '%s', will generate a random one...\n", cat->name);
				} else {
					janus_mutex_lock(&mountpoints_mutex);
					janus_streaming_mountpoint *mp = g_hash_table_lookup(mountpoints, id->value);
					janus_mutex_unlock(&mountpoints_mutex);
					if(mp != NULL) {
						JANUS_LOG(LOG_ERR, "A stream with the provided ID %s already exists, skipping '%s'\n", id->value, cat->name);
						cl = cl->next;
						continue;
					}
				}
				JANUS_LOG(LOG_VERB, "Audio %s, Video %s\n", doaudio ? "enabled" : "NOT enabled", dovideo ? "enabled" : "NOT enabled");
				janus_streaming_mountpoint *mp = NULL;
				if((mp = janus_streaming_create_rtp_source(
						(id && id->value) ? id->value : NULL,
						(char *)cat->name,
						desc ? (char *)desc->value : NULL,
						doaudio,
						amcast ? (char *)amcast->value : NULL,
						(aport && aport->value) ? atoi(aport->value) : 0,
						(acodec && acodec->value) ? atoi(acodec->value) : 0,
						artpmap ? (char *)artpmap->value : NULL,
						afmtp ? (char *)afmtp->value : NULL,
						dovideo,
						vmcast ? (char *)vmcast->value : NULL,
						(vport && vport->value) ? atoi(vport->value) : 0,
						(vcodec && vcodec->value) ? atoi(vcodec->value) : 0,
						vrtpmap ? (char *)vrtpmap->value : NULL,
						vfmtp ? (char *)vfmtp->value : NULL,
						bufferkf)) == NULL) {
					JANUS_LOG(LOG_ERR, "Error creating 'rtp' stream '%s'...\n", cat->name);
					cl = cl->next;
					continue;
				}
				mp->is_private = is_private;
				if(secret && secret->value)
					mp->secret = g_strdup(secret->value);
				if(pin && pin->value)
					mp->pin = g_strdup(pin->value);
			} else if(!strcasecmp(type->value, "live")) {
				/* File live source */
				janus_config_item *id = janus_config_get_item(cat, "id");
				janus_config_item *desc = janus_config_get_item(cat, "description");
				janus_config_item *priv = janus_config_get_item(cat, "is_private");
				janus_config_item *secret = janus_config_get_item(cat, "secret");
				janus_config_item *pin = janus_config_get_item(cat, "pin");
				janus_config_item *file = janus_config_get_item(cat, "filename");
				janus_config_item *audio = janus_config_get_item(cat, "audio");
				janus_config_item *video = janus_config_get_item(cat, "video");
				if(file == NULL || file->value == NULL) {
					JANUS_LOG(LOG_ERR, "Can't add 'live' stream '%s', missing mandatory information...\n", cat->name);
					cl = cl->next;
					continue;
				}
				gboolean is_private = priv && priv->value && janus_is_true(priv->value);
				gboolean doaudio = audio && audio->value && janus_is_true(audio->value);
				gboolean dovideo = video && video->value && janus_is_true(video->value);
				/* TODO We should support something more than raw a-Law and mu-Law streams... */
				if(!doaudio || dovideo) {
					JANUS_LOG(LOG_ERR, "Can't add 'live' stream '%s', we only support audio file streaming right now...\n", cat->name);
					cl = cl->next;
					continue;
				}
				if(!strstr(file->value, ".alaw") && !strstr(file->value, ".mulaw")) {
					JANUS_LOG(LOG_ERR, "Can't add 'live' stream '%s', unsupported format (we only support raw mu-Law and a-Law files right now)\n", cat->name);
					cl = cl->next;
					continue;
				}
				if(id == NULL || id->value == NULL) {
					JANUS_LOG(LOG_VERB, "Missing id for stream '%s', will generate a random one...\n", cat->name);
				} else {
					janus_mutex_lock(&mountpoints_mutex);
					janus_streaming_mountpoint *mp = g_hash_table_lookup(mountpoints, id->value);
					janus_mutex_unlock(&mountpoints_mutex);
					if(mp != NULL) {
						JANUS_LOG(LOG_ERR, "A stream with the provided ID %s already exists, skipping '%s'\n", id->value, cat->name);
						cl = cl->next;
						continue;
					}
				}
				janus_streaming_mountpoint *mp = NULL;
				if((mp = janus_streaming_create_file_source(
						(id && id->value) ? id->value : NULL,
						(char *)cat->name,
						desc ? (char *)desc->value : NULL,
						(char *)file->value,
						TRUE, doaudio, dovideo)) == NULL) {
					JANUS_LOG(LOG_ERR, "Error creating 'live' stream '%s'...\n", cat->name);
					cl = cl->next;
					continue;
				}
				mp->is_private = is_private;
				if(secret && secret->value)
					mp->secret = g_strdup(secret->value);
				if(pin && pin->value)
					mp->pin = g_strdup(pin->value);
			} else if(!strcasecmp(type->value, "ondemand")) {
				/* mu-Law file on demand source */
				janus_config_item *id = janus_config_get_item(cat, "id");
				janus_config_item *desc = janus_config_get_item(cat, "description");
				janus_config_item *priv = janus_config_get_item(cat, "is_private");
				janus_config_item *secret = janus_config_get_item(cat, "secret");
				janus_config_item *pin = janus_config_get_item(cat, "pin");
				janus_config_item *file = janus_config_get_item(cat, "filename");
				janus_config_item *audio = janus_config_get_item(cat, "audio");
				janus_config_item *video = janus_config_get_item(cat, "video");
				if(file == NULL || file->value == NULL) {
					JANUS_LOG(LOG_ERR, "Can't add 'ondemand' stream '%s', missing mandatory information...\n", cat->name);
					cl = cl->next;
					continue;
				}
				gboolean is_private = priv && priv->value && janus_is_true(priv->value);
				gboolean doaudio = audio && audio->value && janus_is_true(audio->value);
				gboolean dovideo = video && video->value && janus_is_true(video->value);
				/* TODO We should support something more than raw a-Law and mu-Law streams... */
				if(!doaudio || dovideo) {
					JANUS_LOG(LOG_ERR, "Can't add 'ondemand' stream '%s', we only support audio file streaming right now...\n", cat->name);
					cl = cl->next;
					continue;
				}
				if(!strstr(file->value, ".alaw") && !strstr(file->value, ".mulaw")) {
					JANUS_LOG(LOG_ERR, "Can't add 'ondemand' stream '%s', unsupported format (we only support raw mu-Law and a-Law files right now)\n", cat->name);
					cl = cl->next;
					continue;
				}
				if(id == NULL || id->value == NULL) {
					JANUS_LOG(LOG_VERB, "Missing id for stream '%s', will generate a random one...\n", cat->name);
				} else {
					janus_mutex_lock(&mountpoints_mutex);
					janus_streaming_mountpoint *mp = g_hash_table_lookup(mountpoints, id->value);
					janus_mutex_unlock(&mountpoints_mutex);
					if(mp != NULL) {
						JANUS_LOG(LOG_ERR, "A stream with the provided ID %s already exists, skipping '%s'\n", id->value, cat->name);
						cl = cl->next;
						continue;
					}
				}
				janus_streaming_mountpoint *mp = NULL;
				if((mp = janus_streaming_create_file_source(
						(id && id->value) ? id->value : NULL,
						(char *)cat->name,
						desc ? (char *)desc->value : NULL,
						(char *)file->value,
						FALSE, doaudio, dovideo)) == NULL) {
					JANUS_LOG(LOG_ERR, "Error creating 'ondemand' stream '%s'...\n", cat->name);
					cl = cl->next;
					continue;
				}
				mp->is_private = is_private;
				if(secret && secret->value)
					mp->secret = g_strdup(secret->value);
				if(pin && pin->value)
					mp->pin = g_strdup(pin->value);
			} else if(!strcasecmp(type->value, "rtsp")) {
#ifndef HAVE_LIBCURL
				JANUS_LOG(LOG_ERR, "Can't add 'rtsp' stream '%s', libcurl support not compiled...\n", cat->name);
				cl = cl->next;
				continue;
#else
				janus_config_item *id = janus_config_get_item(cat, "id");
				janus_config_item *desc = janus_config_get_item(cat, "description");
				janus_config_item *priv = janus_config_get_item(cat, "is_private");
				janus_config_item *secret = janus_config_get_item(cat, "secret");
				janus_config_item *pin = janus_config_get_item(cat, "pin");
				janus_config_item *file = janus_config_get_item(cat, "url");
				janus_config_item *audio = janus_config_get_item(cat, "audio");
				janus_config_item *video = janus_config_get_item(cat, "video");
				if(file == NULL || file->value == NULL) {
					JANUS_LOG(LOG_ERR, "Can't add 'rtsp' stream '%s', missing mandatory information...\n", cat->name);
					cl = cl->next;
					continue;
				}
				gboolean is_private = priv && priv->value && janus_is_true(priv->value);
				gboolean doaudio = audio && audio->value && janus_is_true(audio->value);
				gboolean dovideo = video && video->value && janus_is_true(video->value);
				if(id == NULL || id->value == NULL) {
					JANUS_LOG(LOG_VERB, "Missing id for stream '%s', will generate a random one...\n", cat->name);
				} else {
					janus_mutex_lock(&mountpoints_mutex);
					janus_streaming_mountpoint *mp = g_hash_table_lookup(mountpoints, id->value);
					janus_mutex_unlock(&mountpoints_mutex);
					if(mp != NULL) {
						JANUS_LOG(LOG_ERR, "A stream with the provided ID %s already exists, skipping '%s'\n", id->value, cat->name);
						cl = cl->next;
						continue;
					}
				}
				janus_streaming_mountpoint *mp = NULL;
				if((mp = janus_streaming_create_rtsp_source(
						(id && id->value) ? id->value : NULL,
						(char *)cat->name,
						desc ? (char *)desc->value : NULL,
						(char *)file->value, doaudio, dovideo)) == NULL) {
					JANUS_LOG(LOG_ERR, "Error creating 'rtsp' stream '%s'...\n", cat->name);
					cl = cl->next;
					continue;
				}
				mp->is_private = is_private;
				if(secret && secret->value)
					mp->secret = g_strdup(secret->value);
				if(pin && pin->value)
					mp->pin = g_strdup(pin->value);
#endif
			} else {
				JANUS_LOG(LOG_WARN, "Ignoring unknown stream type '%s' (%s)...\n", type->value, cat->name);
			}
			cl = cl->next;
		}
		/* Done: we keep the configuration file open in case we get a "create" or "destroy" with permanent=true */
	}
	/* Show available mountpoints */
	janus_mutex_lock(&mountpoints_mutex);
	GHashTableIter iter;
	gpointer value;
	g_hash_table_iter_init(&iter, mountpoints);
	while (g_hash_table_iter_next(&iter, NULL, &value)) {
		janus_streaming_mountpoint *mp = value;
		JANUS_LOG(LOG_VERB, "  ::: [%s][%s] %s (%s, %s, %s, pin: %s)\n", mp->id, mp->name, mp->description,
			mp->streaming_type == janus_streaming_type_live ? "live" : "on demand",
			mp->streaming_source == janus_streaming_source_rtp ? "RTP source" : "file source",
			mp->is_private ? "private" : "public",
			mp->pin ? mp->pin : "no pin");
	}
	janus_mutex_unlock(&mountpoints_mutex);

	sessions = g_hash_table_new(NULL, NULL);
	janus_mutex_init(&sessions_mutex);
	messages = g_async_queue_new_full((GDestroyNotify) janus_streaming_message_free);
	/* This is the callback we'll need to invoke to contact the gateway */
	gateway = callback;
	g_atomic_int_set(&initialized, 1);

	GError *error = NULL;
	/* Start the sessions watchdog */
	watchdog = g_thread_try_new("streaming watchdog", &janus_streaming_watchdog, NULL, &error);
	if(!watchdog) {
		g_atomic_int_set(&initialized, 0);
		JANUS_LOG(LOG_ERR, "Got error %d (%s) trying to launch the Streaming watchdog thread...\n", error->code, error->message ? error->message : "??");
		janus_config_destroy(config);
		return -1;
	}
	/* Launch the thread that will handle incoming messages */
	handler_thread = g_thread_try_new("janus streaming handler", janus_streaming_handler, NULL, &error);
	if(error != NULL) {
		g_atomic_int_set(&initialized, 0);
		JANUS_LOG(LOG_ERR, "Got error %d (%s) trying to launch the Streaming handler thread...\n", error->code, error->message ? error->message : "??");
		janus_config_destroy(config);
		return -1;
	}
	JANUS_LOG(LOG_INFO, "%s initialized!\n", JANUS_STREAMING_NAME);
	return 0;
}

void janus_streaming_destroy(void) {
	if(!g_atomic_int_get(&initialized))
		return;
	g_atomic_int_set(&stopping, 1);

	g_async_queue_push(messages, &exit_message);

	if(handler_thread != NULL) {
		g_thread_join(handler_thread);
		handler_thread = NULL;
	}

	/* Remove all mountpoints */
	janus_mutex_lock(&mountpoints_mutex);
	GHashTableIter iter;
	gpointer value;
	g_hash_table_iter_init(&iter, mountpoints);
	while (g_hash_table_iter_next(&iter, NULL, &value)) {
		janus_streaming_mountpoint *mp = value;
		if(!mp->destroyed) {
			mp->destroyed = janus_get_monotonic_time();
			old_mountpoints = g_list_append(old_mountpoints, mp);
		}
	}
	janus_mutex_unlock(&mountpoints_mutex);
	janus_mutex_lock(&transcode_main_loops_mutex);
	g_hash_table_iter_init(&iter, transcode_main_loops);
	while (g_hash_table_iter_next(&iter, NULL, &value)) {
		g_main_loop_quit(value);
	}
	g_hash_table_remove_all(transcode_main_loops);
	g_hash_table_destroy(transcode_main_loops);	
	transcode_main_loops = NULL;
	janus_mutex_unlock(&transcode_main_loops_mutex);
	janus_mutex_lock(&transcode_threads_mutex);	
	g_hash_table_iter_init(&iter, transcode_threads);	
	while (g_hash_table_iter_next(&iter, NULL, &value)) {
		g_thread_join(value);
	}
	g_hash_table_remove_all(transcode_threads);
	g_hash_table_destroy(transcode_threads);
	transcode_threads = NULL;
	janus_mutex_unlock(&transcode_threads_mutex);
	remove_ports(&transcode_ports);
	janus_mutex_lock(&transcode_sources_mutex);	
	g_hash_table_remove_all(transcode_sources);
	g_hash_table_destroy(transcode_sources);
	transcode_sources = NULL;
	janus_mutex_unlock(&transcode_sources_mutex);
	if(watchdog != NULL) {
		g_thread_join(watchdog);
		watchdog = NULL;
	}

	/* FIXME We should destroy the sessions cleanly */
	usleep(500000);
	janus_mutex_lock(&mountpoints_mutex);
	g_hash_table_destroy(mountpoints);
	janus_mutex_unlock(&mountpoints_mutex);
	janus_mutex_lock(&sessions_mutex);
	g_hash_table_destroy(sessions);
	janus_mutex_unlock(&sessions_mutex);
	g_async_queue_unref(messages);
	messages = NULL;
	sessions = NULL;

	janus_config_destroy(config);

	g_atomic_int_set(&initialized, 0);
	g_atomic_int_set(&stopping, 0);
	JANUS_LOG(LOG_INFO, "%s destroyed!\n", JANUS_STREAMING_NAME);
}

